INSERT INTO `jf_cms_content_ext` VALUES ('14', '养生,瑜伽,秀逗啊', '瑜伽（英文：Yoga，印地语：योग）是一个汉语词汇，最早是从印度梵语“yug”或“yuj”而来，其含意为“一致”、“结合”或“和谐”。瑜伽源于古印度，是古印度六大哲学派别中的一系，探寻“梵我合一”的道理与方法。而现代人所称的瑜伽运动则是主要是一系列的修身养心方法。', '<p><span style=\"color: rgb(51, 51, 51); font-family: Verdana, Arial, 微软雅黑, Helvetica, sans-serif, SimSun; font-size: 15px; letter-spacing: 1px; line-height: 28px; background-color: rgb(255, 255, 255);\">文章引自：小清新（http://aladd.net/archives/32310.html）</span></p><p><strong style=\"font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;PingFang SC&#39;, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, &#39;Noto Sans CJK SC&#39;, &#39;WenQuanYi Micro Hei&#39;, Arial, sans-serif; font-size: 18px; letter-spacing: 1px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);\">瑜伽</strong><span style=\"font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;PingFang SC&#39;, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, &#39;Noto Sans CJK SC&#39;, &#39;WenQuanYi Micro Hei&#39;, Arial, sans-serif; font-size: 18px; letter-spacing: 1px; line-height: 30px; background-color: rgb(255, 255, 255);\">（英文：Yoga，印地语：योग）是一个汉语词汇，最早是从印度梵语“yug”或“yuj”而来，其含意为“一致”、“结合”或“和谐”。瑜伽源于古印度，是古印度六大哲学派别中的一系，探寻“梵我合一”的道理与方法。而现代人所称的瑜伽</span><a href=\"http://aladd.net/archives/tag/%E8%BF%90%E5%8A%A8%E5%9B%BE%E7%89%87\" style=\"color: rgb(85, 85, 78); text-decoration: none; font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;PingFang SC&#39;, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, &#39;Noto Sans CJK SC&#39;, &#39;WenQuanYi Micro Hei&#39;, Arial, sans-serif; font-size: 18px; letter-spacing: 1px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);\">运动</a><span style=\"font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;PingFang SC&#39;, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, &#39;Noto Sans CJK SC&#39;, &#39;WenQuanYi Micro Hei&#39;, Arial, sans-serif; font-size: 18px; letter-spacing: 1px; line-height: 30px; backgroun